import { categoryAction } from './categorySlice';
import { call, put, takeLatest } from "@redux-saga/core/effects";
import { Category, DataResponse } from "models";
import categoryApi from 'api/categoryApi';
import { toast } from 'react-toastify';

function* fetchCategoryList(){
    try {
        const response: DataResponse<Category> = yield call(categoryApi.getAll);
        if(response.success){
            yield put(categoryAction.fetchCategoryListSuccess(response));
        }
        else{
            toast.error(response.content.message);
            yield put(categoryAction.fetchCategoryListFailed(response.content.message));
        }
    } catch (error) {
        console.log(error)
    }
}

export default function* categorySaga() {
    yield takeLatest(categoryAction.fetchCategoryList.type, fetchCategoryList);
}
