import {
  Box,
  Button,
  ButtonGroup,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Divider,
  makeStyles,
  Paper,
  Typography,
} from '@material-ui/core'
import { Note } from 'models'
import moment from 'moment'
import React, { useState } from 'react'

export interface NoteItemProps {
  note: Note
}

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    height: '100%',
    maxWidth: 360,
    minHeight: 250,
    backgroundColor: theme.palette.background.paper,
  },
  chip: {
    margin: theme.spacing(0.5),
  },
  section1: {
    padding: theme.spacing(2, 2),
  },
  section2: {
    margin: theme.spacing(2),
  },
  section3: {
    margin: theme.spacing(1, 1, 1),
    float: 'right',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
}))

export default function NoteItem({ note }: NoteItemProps) {
  const classes = useStyles()
  const [openContent, setOpenContent] = useState(false)
  const handleCloseContent = () => {
    setOpenContent(false)
  }
  //   const [status, setStatus] = React.useState('')

  //   const handleChangeSelect = (event: React.ChangeEvent<{ value: unknown }>) => {
  //     setStatus(event.target.value as string)
  //   }
  console.log(new Date(note.completedAt).valueOf() - new Date().valueOf())
  console.log('---------------------------------------------------------')
  return (
    <Paper elevation={3} className={classes.root} c>
      <Box className={classes.section1} onClick={() => setOpenContent(true)}>
        <Typography gutterBottom variant="h4">
          {note.title}
        </Typography>
      </Box>
      <Divider variant="middle" />
      <Box className={classes.section1}>
        <Typography color="textPrimary" variant="body1">
          Completed: {moment(note.completedAt).format('HH:MM MMM DD, YYYY')}
        </Typography>
      </Box>
      <Divider variant="middle" />
      <Box className={classes.section2}>
        <Typography gutterBottom variant="body1">
          Note status: {note.status}
        </Typography>
        {/* <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={status}
          onChange={handleChangeSelect}
        >
          <MenuItem value="NOT COMPLETED">Not Completed</MenuItem>
          <MenuItem value="COMPLETED">Completed</MenuItem>
        </Select> */}
      </Box>
      <Box className={classes.section3}>
        <ButtonGroup>
          <Button size="small" variant="contained" color="primary">
            Edit
          </Button>
          <Button size="small" variant="contained" color="secondary">
            Delete
          </Button>
        </ButtonGroup>
      </Box>

      <Dialog
        open={openContent}
        onClose={handleCloseContent}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
        maxWidth="md"
      >
        <DialogTitle id="alert-dialog-title">
          <Typography gutterBottom variant="h4">
            {note.title}
          </Typography>
          <Typography color="textPrimary" variant="body1">
            Completed: {moment(note.completedAt).format('HH:MM MMM DD, YYYY')}
          </Typography>
        </DialogTitle>
        <DialogContent>{note.content}</DialogContent>
        <DialogActions>
          <Button onClick={handleCloseContent} color="secondary" variant="outlined" autoFocus>
            Cancel
          </Button>
        </DialogActions>
      </Dialog>
    </Paper>
  )
}
