import { yupResolver } from '@hookform/resolvers/yup';
import { Box, Button, CircularProgress, makeStyles } from '@material-ui/core';
import { InputField } from 'components/FormFields';
import { Post } from 'models';
import React from 'react';
import { useForm } from 'react-hook-form';
import * as yup from "yup";

const schema = yup.object().shape({
    
});

export interface PostFormProps {
    initialValue: Post;
    onSubmit: (formValue: Post) => void;
}

const useStyles = makeStyles(theme => ({
    root: {
        textAlign: 'center',
    }
}))

export default function PostForm ({initialValue, onSubmit}: PostFormProps) {

    const {
        control,
        handleSubmit,
        formState: {isSubmitting}
    } = useForm<Post>({
        defaultValues: initialValue,
        resolver: yupResolver(schema)
    });

    const handleSubmitForm = async (formValue: Post) => {
        try {
            console.log(formValue);
            await onSubmit?.(formValue);
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <Box maxWidth={600}>
            <form onSubmit={handleSubmit(handleSubmitForm)}>
                <InputField name="title" control={control} label="Title"/>
                <InputField name="content" control={control} label="Content"/>
                <InputField name="imageUrl" control={control} label="Image"/>

                <Box mt={3}>
                    <Button type="submit" variant="contained" color="primary" disabled={isSubmitting}>
                        {isSubmitting && <CircularProgress size={20} color="primary"/>} Save
                    </Button>
                </Box>
            </form>
        </Box>
    );
}
