import { DataResponse } from 'models/common';
import { Post } from 'models/post';
import axiosClient from './axiosClient';

const postApi = {
    getAll(): Promise<DataResponse<Post>> {
        const url = '/api/posts/get-all-post';
        return axiosClient.get(url);
    },
    getPostById(id: string): Promise<Post> {
        const url = `/api/post/get-by-id/${id}`;
        return axiosClient.get(url);
    },
    add(data: Post): Promise<Post> {
        const url = '/api/post/add';
        return axiosClient.post(url,{data});
    },
    update(data: Post): Promise<Post> {
        const url = `/api/post/update/${data.id}`;
        return axiosClient.patch(url,{data});
    },
    remove(id: string): Promise<any> {
        const url = '/api/post/remove';
        return axiosClient.delete(url);
    }
}

export default postApi;
