import {
  Box,
  Button,
  Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Fab,
  IconButton,
  LinearProgress,
  makeStyles,
  Theme,
  Tooltip,
  Typography
} from '@material-ui/core'
import MuiDialogActions from '@material-ui/core/DialogActions'
import MuiDialogContent from '@material-ui/core/DialogContent'
import MuiDialogTitle from '@material-ui/core/DialogTitle'
import { Add } from '@material-ui/icons'
import CloseIcon from '@material-ui/icons/Close'
import { createStyles, WithStyles, withStyles } from '@material-ui/styles'
import { useAppDispatch, useAppSelector } from 'app/hooks'
import React, { useEffect, useState } from 'react'
import { noteAction, selectNoteLoading } from '../noteSlice'

const useStyles = makeStyles((theme) => ({
  root: {
    position: 'relative',
    paddingTop: theme.spacing(1),
  },

  loading: {
    position: 'absolute',
    top: theme.spacing(-1),
    width: '100%',
  },

  fab: {
    position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
  },
}))


export default function NoteListPage() {
  const classes = useStyles()
  const loading = useAppSelector(selectNoteLoading)
  const dispatch = useAppDispatch()
  const [openFormAdd, setOpenFormAdd] = useState(false)
  useEffect(() => {
    dispatch(noteAction.fetchNoteList)
  }, [dispatch])

  const handleCloseFormAdd = () => {
    setOpenFormAdd(false)
  }
  return (
    <Box className={classes.root}>
      {loading && <LinearProgress className={classes.loading} />}
      <Tooltip title="Add New Note" aria-label="add">
        <Fab className={classes.fab} color="primary" size="large" onClick={() => setOpenFormAdd(true)}>
          <Add />
        </Fab>
      </Tooltip>

      <Dialog
        open={openFormAdd}
        onClose={handleCloseFormAdd}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">Remove post</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Are you sure remove post title: "{post?.title}". <br />
            This action can&apos;t undo.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseFormAdd} color="primary" variant="outlined">
            Cancel
          </Button>
          <Button onClick={handleRemovePost} color="secondary" variant="contained" autoFocus>
            Remove
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  )
}
