import {
  Box,
  Button,
  Dialog, Fab,
  IconButton,
  LinearProgress,
  makeStyles,
  Theme,
  Tooltip,
  Typography
} from '@material-ui/core'
import MuiDialogActions from '@material-ui/core/DialogActions'
import MuiDialogContent from '@material-ui/core/DialogContent'
import MuiDialogTitle from '@material-ui/core/DialogTitle'
import { Add } from '@material-ui/icons'
import CloseIcon from '@material-ui/icons/Close'
import { createStyles, WithStyles, withStyles } from '@material-ui/styles'
import { useAppDispatch, useAppSelector } from 'app/hooks'
import React, { useEffect, useState } from 'react'
import { noteAction, selectNoteLoading } from '../noteSlice'

const useStyles = makeStyles((theme) => ({
  root: {
    position: 'relative',
    paddingTop: theme.spacing(1),
  },

  loading: {
    position: 'absolute',
    top: theme.spacing(-1),
    width: '100%',
  },

  fab: {
    position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
  },
}))

const styles = (theme: Theme) =>
  createStyles({
    root: {
      margin: 0,
      padding: theme.spacing(2),
    },
    closeButton: {
      position: 'absolute',
      right: theme.spacing(1),
      top: theme.spacing(1),
      color: theme.palette.grey[500],
    },
  })

export interface DialogTitleProps extends WithStyles<typeof styles> {
  id: string
  children: React.ReactNode
  onClose: () => void
}

const DialogTitle = withStyles(styles)((props: DialogTitleProps) => {
  const { children, classes, onClose, ...other } = props
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  )
})

const DialogContent = withStyles((theme: Theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiDialogContent)

const DialogActions = withStyles((theme: Theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1),
  },
}))(MuiDialogActions)

export default function NoteListPage() {
  const classes = useStyles()
  const loading = useAppSelector(selectNoteLoading)
  const dispatch = useAppDispatch()
  const [openFormAdd, setOpenFormAdd] = useState(false)
  useEffect(() => {
    dispatch(noteAction.fetchNoteList)
  }, [dispatch])

  const handleCloseFormAdd = () => {
    setOpenFormAdd(false)
  }
  return (
    <Box className={classes.root}>
      {loading && <LinearProgress className={classes.loading} />}
      <Tooltip title="Add New Note" aria-label="add">
        <Fab className={classes.fab} color="primary" size="large" onClick={() => setOpenFormAdd(true)}>
          <Add />
        </Fab>
      </Tooltip>

      
    </Box>
  )
}
