import { RootState } from 'app/store'
import { createSlice } from '@reduxjs/toolkit'
import { Note } from 'models'

export interface NoteState {
  loading: boolean
  noteList: Note[]
}

const initialState: NoteState = {
  loading: false,
  noteList: [],
}

const noteSlice = createSlice({
  name: 'note',
  initialState,
  reducers: {
    fetchPostList(state){
        state.loading = true;
    },
    fetchPostListSuccess(state, action: PayloadAction<ListResponse<Post>>){
        state.loading = false;
        state.list = action.payload.content.data;
    },
    fetchPostListFailed(state, action: PayloadAction<string>){
        state.loading = false;
    },
  },
})

// Action
export const noteAction = noteSlice.actions
// Selector
export const selectNoteLoading = (state: RootState) => state.note.loading
export const selectNoteList = (state: RootState) => state.note.noteList
// Reducer
const noteReducer = noteSlice.reducer
export default noteReducer
